import React, { useState, useRef } from "react";
import YouTube from "react-youtube";

const YoutubePlayer = ({ playlist }) => {
	const [player, setPlayer] = useState(null);
	const [currentIndex, setCurrentIndex] = useState(0);
	const [isPlaying, setIsPlaying] = useState(false);

	const playerRef = useRef(null);

	const handlePlayerReady = (event) => {
		setPlayer(event.target);
	};

	const handlePlay = () => {
		setIsPlaying(true);
		player.playVideo();
	};

	const handlePause = () => {
		setIsPlaying(false);
		player.pauseVideo();
	};

	const handleNext = () => {
		setCurrentIndex((prevIndex) => (prevIndex + 1) % playlist.length);
	};

	const handlePrevious = () => {
		setCurrentIndex((prevIndex) => {
			const newIndex = prevIndex - 1;
			return newIndex < 0 ? playlist.length - 1 : newIndex;
		});
	};

	const handlePlaylistEnd = () => {
		setCurrentIndex(0);
	};

	const opts = {
		playerVars: {
			autoplay: 0,
			controls: 0,
			listType: "playlist",
			list: playlist.join(","),
		},
	};

	return (
		<div className="Youtube-Player">
			<YouTube videoId={playlist[currentIndex]} opts={opts} onReady={handlePlayerReady} onEnd={handlePlaylistEnd} containerClassName="youtube-container" className="youtube-player" ref={playerRef} />
			<div className="player-controls">
				{!isPlaying && <button onClick={handlePlay}>Play</button>}
				{isPlaying && <button onClick={handlePause}>Pause</button>}
				<button onClick={handlePrevious}>Previous</button>
				<button onClick={handleNext}>Next</button>
			</div>
		</div>
	);
};

export default YoutubePlayer;
