import React, { useState } from "react";
import Playlist from "./Playlist";

const DropdownMenuYoutube = ({ playlists, handlePlaylistSelect, token, logout, handleButtonClick }) => {
	const [isOpen, setIsOpen] = useState(false);

	const toggleDropdown = () => {
		setIsOpen(!isOpen);
	};

	return (
		<div className="youtube-playlists">
			{/* <div className="spotify">
         
          
         {!token ?  <button id='login' onClick={handleButtonClick}>Login to Spotify</button>
             : <button id='logout' onClick={logout}> Logout</button>
         }
      </div> */}
			{/* <button className="y-dropdown-toggle" onClick={toggleDropdown}>
				Youtube Playlists
			</button> */}
			{isOpen && (
				<div className="youtube-playlists">
					{/* {playlists.map((playlist) => (
						<Playlist key={playlist.id} title={playlist.name} onClick={() => handlePlaylistSelect(playlist)} />
					))} */}
				</div>
			)}
		</div>
	);
};

export default DropdownMenuYoutube;
