import React, { useState, useRef, useEffect } from "react";
import YouTube from "react-youtube";

const YoutubePlayer = ({ youtubePlaylist }) => {
	const [player, setPlayer] = useState(null);
	const [youtubeCurrentIndex, setYoutubeCurrentIndex] = useState(0);
	const [youtubeIsPlaying, setYoutubeIsPlaying] = useState(false);

	const handlePlayerReady = (event) => {
		setPlayer(event.target);
		console.log("Player Ready:", event.target);
	};

	const handlePlay = () => {
		setYoutubeIsPlaying(true);
		player.playVideo();
	};

	const handlePause = () => {
		setYoutubeIsPlaying(false);
		player.pauseVideo();
	};

	const handleNext = () => {
		setYoutubeCurrentIndex((prevIndex) => (prevIndex + 1) % youtubePlaylist.length);
	};

	const handlePrevious = () => {
		setYoutubeCurrentIndex((prevIndex) => {
			const newIndex = prevIndex - 1;
			return newIndex < 0 ? youtubePlaylist.length - 1 : newIndex;
		});
	};

	const handlePlaylistEnd = () => {
		setYoutubeCurrentIndex(0);
	};

	const opts = {
		playerVars: {
			autoplay: 0,
			controls: 0,
			listType: "playlist",
			list: youtubePlaylist.join(","),
		},
	};

	return (
		<div className="Youtube-Player">
			<YouTube videoId={youtubePlaylist[youtubeCurrentIndex]} opts={opts} onReady={handlePlayerReady} onEnd={handlePlaylistEnd} containerClassName="youtube-container" className="youtube-player" />
			<div className="player-controls">
				{!youtubeIsPlaying && <button onClick={handlePlay}>Play</button>}
				{youtubeIsPlaying && <button onClick={handlePause}>Pause</button>}
				<button onClick={handlePrevious}>Previous</button>
				<button onClick={handleNext}>Next</button>
			</div>
		</div>
	);
};

export default YoutubePlayer;
