import React, { useEffect, useState } from "react";

const SpotifyPlayer = ({ accessToken, currentTrack }) => {
	const [player, setPlayer] = useState(null);
	const [isPaused, setPaused] = useState(false);
	const [is_active, setActive] = useState(false);

	useEffect(() => {
		const script = document.createElement("script");
		script.src = "https://sdk.scdn.co/spotify-player.js";
		script.async = true;
		document.body.appendChild(script);
		window.onSpotifyWebPlaybackSDKReady = () => {
			const player = new window.Spotify.Player({
				name: "Web Playback SDK",
				getOAuthToken: (cb) => {
					cb(accessToken);
				},
				volume: 0.5,
			});

			setPlayer(player);

			player.addListener("ready", ({ device_id }) => {
				console.log("Ready with Device ID", device_id);
			});

			player.addListener("not_ready", ({ device_id }) => {
				console.log("Device ID has gone offline", device_id);
			});

			player.addListener("player_state_changed", (state) => {
				if (!state) {
					return;
				}

				setTrack(state.track_window.current_track);
				setPaused(state.paused);

				player.getCurrentState().then((state) => {
					!state ? setActive(false) : setActive(true);
				});
			});

			player.connect();
		};
	}, []);

	// if (window.Spotify && window.Spotify.Player) {
	// 	initializePlayer();
	// } else {
	// 	script.addEventListener("load", initializePlayer);
	// }

	// 	return () => {
	// 		if (player !== null) {
	// 			player.disconnect();
	// 		}
	// 	};
	// }, [accessToken]);

	useEffect(() => {
		if (player !== null && currentTrack?.uri) {
			player.setVolume(0.5).then(() => {
				player._options.getOAuthToken((access_token) => {
					fetch(`https://api.spotify.com/v1/me/player/play`, {
						method: "PUT",
						body: JSON.stringify({
							uris: [currentTrack?.uri],
						}),
						headers: {
							"Content-Type": "application/json",
							Authorization: `Bearer ${access_token}`,
						},
					});
				});
			});
		}
	}, [player, currentTrack?.uri]);

	useEffect(() => {
		if (player !== null && currentTrack) {
			player.resume();
		}
	}, [player, currentTrack]);

	const handlePreviousTrack = () => {
		if (player !== null) {
			player.previousTrack();
		}
	};

	const handleTogglePlay = () => {
		if (player !== null) {
			player.togglePlay();
			setPaused(!isPaused);
		}
	};

	const handleNextTrack = () => {
		if (player !== null) {
			player.nextTrack();
		}
	};

	if (currentTrack != null) {
		return (
			<div id="spotify-player">
				<div className="main-wrapper">
					<img src={currentTrack.album.images[0].url} className="now-playing__cover" alt="" />
					<div className="now-playing__side">
						<div className="now-playing__name">{currentTrack.name}</div>
						<div className="now-playing__artist">{currentTrack.artists[0].name}</div>
						<button className="btn-spotify" onClick={handlePreviousTrack}>
							&lt;&lt;
						</button>
						<button className="btn-spotify" onClick={handleTogglePlay}>
							{isPaused ? "PLAY" : "PAUSE"}
						</button>
						<button className="btn-spotify" onClick={handleNextTrack}>
							&gt;&gt;
						</button>
					</div>
				</div>
			</div>
		);
	} else {
		return null;
	}
};

export default SpotifyPlayer;
