import { useState, useEffect } from "react";
import SpotifyPlayer from "react-spotify-web-playback";

export default function Player({ accessToken, trackUri, playlistUri }) {
	const [play, setPlay] = useState(false);
	const [currentTrackUri, setCurrentTrackUri] = useState(trackUri);

	useEffect(() => {
		setPlay(true);
		setCurrentTrackUri(trackUri);
	}, [trackUri]);

	const handlePlaybackState = (state) => {
		if (!state.isPlaying) {
			setPlay(false);
		}

		// Get the currently playing song
		const currentlyPlayingSong = state.trackWindow.currentTrack;
		console.log(currentlyPlayingSong);
	};

	if (!accessToken) {
		return null;
	}
	return (
		<SpotifyPlayer
			styles={{
				activeColor: "#fff",
				bgColor: "#242424",
				color: "#fff",
				loaderColor: "#fff",
				sliderColor: "#1cb954",
				trackArtistColor: "#ccc",
				trackNameColor: "#fff",
			}}
			token={accessToken}
			showSaveIcon
			callback={handlePlaybackState}
			play={play}
			uris={playlistUri}
		/>
	);
}
