import React from "react";
import DropdownMenuSpotify from "./DropdownMenuSpotify";
import YoutubePlaylists from "./YoutubePlaylists ";

const ControlPanel = ({ playlists, handlePlaylistSelect, YoutubePlaylists }) => {
	// const handleButtonClick = () => {
	// 	const loginUrl = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`;
	// 	window.location.href = loginUrl;
	// };

	return (
		<div className="controlPanel">
			<DropdownMenuSpotify playlists={playlists} handlePlaylistSelect={handlePlaylistSelect}></DropdownMenuSpotify>
			<YoutubePlaylists YoutubePlaylists={YoutubePlaylists} />
		</div>
	);
};

export default ControlPanel;
