import React, { useEffect, useState } from "react";

const SpotifyWebPlayer = ({ accessToken, currentTrack }) => {
	const [isPlaying, setIsPlaying] = useState(false);
	const [player, setPlayer] = useState(undefined);

	useEffect(() => {
		const script = document.createElement("script");
		script.src = "https://sdk.scdn.co/spotify-player.js";
		script.async = true;

		document.body.appendChild(script);

		window.onSpotifyWebPlaybackSDKReady = () => {
			const player = new window.Spotify.Player({
				name: "Web Playback SDK",
				getOAuthToken: (cb) => {
					cb(accessToken);
				},
				volume: 0.5,
			});

			setPlayer(player);

			player.addListener("ready", () => {
				console.log("The Spotify player is ready.");
				// player
				// 	.resume()
				// 	.then(() => {
				// 		setIsPlaying(true);
				// 	})
				// 	.catch((error) => {
				// 		console.error("Failed to start playback:", error);
				// 	});
			});

			// player.addListener("player_state_changed", (state) => {
			// 	setIsPlaying(state.paused === false);
			// });

			player.connect().then((success) => {
				if (success) {
					console.log("The Spotify player is connected.");
				}
			});
		};
	}, [accessToken]);

	const handlePlayPause = () => {
		// 	if (isPlaying) {
		// 		player.pause().then(() => {
		// 			setIsPlaying(false);
		// 		});
		// 	} else {
		// 		player.resume().then(() => {
		// 			setIsPlaying(true);
		// 		});
		// 	}
	};
	if (currentTrack !== null) {
		return (
			<div>
				<div className="container">
					<div className="main-wrapper">
						<img src={currentTrack.album.images[0].url} className="now-playing__cover" alt="" />

						<div className="now-playing__side">
							<div className="now-playing__name">{currentTrack.name}</div>
							<div className="now-playing__artist">{currentTrack.artists[0].name}</div>

							<button
								className="btn-spotify"
								onClick={() => {
									player.previousTrack();
								}}
							>
								&lt;&lt;
							</button>

							<button
								className="btn-spotify"
								onClick={() => {
									player.togglePlay();
								}}
							>
								{is_paused ? "PLAY" : "PAUSE"}
							</button>

							<button
								className="btn-spotify"
								onClick={() => {
									player.nextTrack();
								}}
							>
								&gt;&gt;
							</button>
						</div>
					</div>
				</div>
			</div>
		);
	} else {
		return;
	}
};

export default SpotifyWebPlayer;
