import "bootstrap/dist/css/bootstrap.min.css";
import Login from "./Login";
import Dashboard from "./Dashboard";

const code = new URLSearchParams(window.location.search).get("code");

function App() {
	return code ? (
		<>
			<hr />
			<div>
				<ControlPanel token={token} AUTH_ENDPOINT={AUTH_ENDPOINT} CLIENT_ID={CLIENT_ID} REDIRECT_URI={REDIRECT_URI} RESPONSE_TYPE={RESPONSE_TYPE} playlists={playlists} handlePlaylistSelect={handlePlaylistSelect} logout={logout} />
				<Tracks selectedPlaylist={selectedPlaylist} playlistTracks={playlistTracks} playTrack={playTrack} />
				<MusicPlayer accessToken={token} trackUri={selectedTrackUri} />
			</div>
			<Dashboard code={code} />
		</>
	) : (
		<Login />
	);
}

export default App;
