import { useState, useEffect } from "react";
import SpotifyPlayer from "react-spotify-web-playback";

export default function Player({ accessToken, trackUri, playlistUri }) {
	const [play, setPlay] = useState(false);
	const [currentSongIndex, setCurrentSongIndex] = useState(0); // Add state for the current song index

	useEffect(() => {
		setPlay(true);
		const newIndex = playlistUri.findIndex((uri) => uri === trackUri);
		setCurrentSongIndex(newIndex);
		console.log(newIndex); // Find the new index based on the provided trackUri
	}, [trackUri]);
	// activeColor: string;
	//   altColor: string;
	//   bgColor: string;
	//   color: string;
	//   errorColor: string;
	//   height: number;
	//   loaderColor: string;
	//   loaderSize: number | string;
	//   sliderColor: string;
	//   sliderHandleBorderRadius: number | string;
	//   sliderHandleColor: string;
	//   sliderHeight: number;
	//   sliderTrackBorderRadius: number | string;
	//   sliderTrackColor: string;
	//   trackArtistColor: string;
	//   trackNameColor: string;
	// Layout: "responsive",
	return (
		<SpotifyPlayer
			styles={{
				activeColor: "#202528",
				bgColor: "#202528",
				color: "#fff",
				loaderColor: "#0011ff",
				sliderColor: "#0011ff",
				trackArtistColor: "#ccc",
				trackNameColor: "#fff",
				sliderTrackBorderRadius: "100",
			}}
			token={accessToken}
			// showSaveIcon
			callback={(state) => {
				if (!state.isPlaying) setPlay(false);
				const currentSongIndex = playlistUri.findIndex((uri) => uri === state.track?.uri);
				console.log(currentSongIndex);
			}}
			magnifySliderOnHover={true}
			offset={currentSongIndex}
			// shuffle={true}
			play={play}
			uris={playlistUri}
		/>
	);
}
