import React, { useState, useRef, useEffect } from "react";
import YouTube from "react-youtube";

const YoutubePlayer = ({ playlist }) => {
	const [player, setPlayer] = useState(null);
	const [currentIndex, setCurrentIndex] = useState(0);
	const [isPlaying, setIsPlaying] = useState(false);
	const [currentTime, setCurrentTime] = useState(0);
	const [duration, setDuration] = useState(0);

	const playerRef = useRef(null);

	const handlePlayerReady = (event) => {
		setPlayer(event.target);
		setDuration(event.target.getDuration());
	};

	const handlePlay = () => {
		setIsPlaying(true);
		player.playVideo();
	};

	const handlePause = () => {
		setIsPlaying(false);
		player.pauseVideo();
	};

	const handleNext = () => {
		setCurrentIndex((prevIndex) => prevIndex + 1);
	};

	const handlePrevious = () => {
		setCurrentIndex((prevIndex) => prevIndex - 1);
	};

	const handlePlaylistEnd = () => {
		// Handle playlist end logic here
		// For example, you can stop playback or loop back to the beginning
		setCurrentIndex(0);
	};
	const handleProgress = (event) => {
		setCurrentTime(event.target.getCurrentTime());
	};

	const formatTime = (time) => {
		const minutes = Math.floor(time / 60);
		const seconds = Math.floor(time % 60);
		return `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
	};

	// useEffect(() => {
	// 	if (player) {
	// 		const playlistIds = playlist.songs.map((song) => song.id).join(",");
	// 		player.loadPlaylist({
	// 			listType: "playlist",
	// 			list: playlistIds,
	// 			index: currentIndex,
	// 		});
	// 		player.playVideoAt(currentIndex);
	// 	}
	// }, [player, currentIndex, playlist]);
	const playlistIds = playlist.songs.map((song) => song.id).join(",");
	const opts = {
		playerVars: {
			autoplay: 0,
			controls: 0,
			listType: "playlist",
			list: playlistIds,
		},
	};

	return (
		<div className="Youtube-Player">
			<div className="progress-bar">
				<div className="progress-bar__current" style={{ width: `${(currentTime / duration) * 100}%` }} />
			</div>
			<div className="player-controls">
				{!isPlaying && <button onClick={handlePlay}>Play</button>}
				{isPlaying && <button onClick={handlePause}>Pause</button>}
				<button onClick={handlePrevious}>Previous</button>
				<button onClick={handleNext}>Next</button>
			</div>
			<div className="time">
				<span>{formatTime(currentTime)}</span> / <span>{formatTime(duration)}</span>
			</div>
			<YouTube videoId={playlist.songs[currentIndex].id} opts={opts} onReady={handlePlayerReady} onStateChange={handleProgress} />
		</div>
	);
};

export default YoutubePlayer;

