import { useState } from "react";

const SpotifyPlaylist = ({ title, handleClick, onDoubleClick }) => {
	// console.log(title);
	const [isSelected, setIsSelected] = useState(false);

	const handlePlaylistClick = () => {
		setIsSelected(!isSelected);
		handleClick();
	};
	return (
		<div className={`playlist ${isSelected ? "selected" : ""}`} onClick={handlePlaylistClick}>
			<p>{title}</p>
		</div>
	);
};

SpotifyPlaylist.defaultProps = {
	title: "Title?",
};

export default SpotifyPlaylist;
