import React, { useState } from "react";
import Playlist from "./Playlist";

const DropdownMenuSpotify = ({ playlists, handlePlaylistSelect }) => {
	// const [isOpen, setIsOpen] = useState(false);

	// const toggleDropdown = () => {
	// 	setIsOpen(!isOpen);
	// };

	return (
		<div className="Spotify-Playlists">
			{/* 			
				<div className="spotify"> 
		         {!token ?  <button id='login' onClick={handleButtonClick}>
					 Login to Spotify 
					</button> : <button id='logout' onClick={logout}> Logout</button>}
	      	</div>
			*/}
			{/* 
				<button className="s-dropdown-toggle" onClick={toggleDropdown}> Spotify Playlists </button>
			*/}

			<h3>Spotify Playlists</h3>
			<div className="playlists">
				{playlists.map((playlist) => (
					<Playlist key={playlist.id} title={playlist.name} onClick={() => handlePlaylistSelect(playlist)} />
				))}
			</div>
			{/* )} */}
		</div>
	);
};

export default DropdownMenuSpotify;
