import { useState, useEffect } from "react";
import useAuth from "./useAuth";
import Player from "./Player";
import TrackSearchResult from "./TrackSearchResult";
import { Container, Form } from "react-bootstrap";
import SpotifyWebApi from "spotify-web-api-node";
import axios from "axios";

const spotifyApi = new SpotifyWebApi({
	clientId: "b96044a084a542c691fe9b0eca9684de",
});

export default function Dashboard({ code }) {
	const accessToken = useAuth(code);
	const [search, setSearch] = useState("");
	const [searchResults, setSearchResults] = useState([]);
	const [playingTrack, setPlayingTrack] = useState();
	const [lyrics, setLyrics] = useState("");
	console.log(searchResults);

	function chooseTrack(track) {
		setPlayingTrack(track);
		setSearch("");
		setLyrics("");
	}

	useEffect(() => {
		if (!playingTrack) return;

		axios
			.get("http://localhost:3001/lyrics", {
				params: {
					track: playingTrack.title,
					artist: playingTrack.artist,
				},
			})
			.then((res) => {
				setLyrics(res.data.lyrics);
			});
	}, [playingTrack]);

	useEffect(() => {
		if (!accessToken) return;
		spotifyApi.setAccessToken(accessToken);
	}, [accessToken]);

	useEffect(() => {
		if (!search) return setSearchResults([]);
		if (!accessToken) return;

		// let cancel = false;
		spotifyApi.searchTracks(search).then((res) => {
			console.log(res.body.tracks.items);
			// if (cancel) return;
			setSearchResults(
				res.body.tracks.items.map((track) => {
					const smallestAlbumImage = track.album.images.reduce((smallest, image) => {
						if (image.height < smallest.height) return image;
						return smallest;
					}, track.album.images[0]);

					return {
						artist: track.artists[0].name,
						title: track.name,
						uri: track.uri,
						albumUrl: smallestAlbumImage.url,
					};
				})
			);
		});

		// return () => (cancel = true);
	}, [search, accessToken]);

	return (
		<Container className="d-flex flex-column py-2" style={{ height: "100vh" }}>
			<ControlPanel token={token} AUTH_ENDPOINT={AUTH_ENDPOINT} CLIENT_ID={CLIENT_ID} REDIRECT_URI={REDIRECT_URI} RESPONSE_TYPE={RESPONSE_TYPE} playlists={playlists} handlePlaylistSelect={handlePlaylistSelect} logout={logout} />
			<Tracks selectedPlaylist={selectedPlaylist} playlistTracks={playlistTracks} playTrack={playTrack} />
			<Player accessToken={accessToken} trackUri={playingTrack?.uri} />
			<Player accessToken={accessToken} trackUri={playingTrack?.uri} />
			{/* </div> */}
		</Container>
	);
}
