import React, { useEffect, useState } from "react";

const SPOTIFY_SDK_URL = "https://sdk.scdn.co/spotify-player.js";

const SpotifyWebPlayer = ({ accessToken, currentTrack }) => {
	const [isPlaying, setIsPlaying] = useState(false);
	let player;

	useEffect(() => {
		const script = document.createElement("script");
		script.src = SPOTIFY_SDK_URL;
		script.async = true;
		document.body.appendChild(script);

		window.onSpotifyWebPlaybackSDKReady = () => {
			player = new window.Spotify.Player({
				name: "React Spotify Player",
				getOAuthToken: (callback) => {
					callback(accessToken);
				},
				volume: 0.5,
			});

			player.addListener("ready", ({ device_id }) => {
				console.log("The Spotify player is ready.");
				player.setCurrentPlaybackDevice(device_id);
				player.queue({ uri: currentTrack?.uri });
			});

			player.addListener("player_state_changed", (state) => {
				setIsPlaying(state.paused === false);
			});

			player.connect().then((success) => {
				if (success) {
					console.log("The Spotify player is connected.");
				}
			});
		};
	}, [accessToken, currentTrack?.uri]);

	const handlePlayPause = () => {
		if (isPlaying) {
			player.pause();
			setIsPlaying(false);
		} else {
			player.resume();
			setIsPlaying(true);
		}
	};

	return (
		<div>
			<div id="spotify-web-player"></div>
			<button onClick={handlePlayPause}>{isPlaying ? "Pause" : "Play"}</button>
		</div>
	);
};

export default SpotifyWebPlayer;
