import React, { useEffect, useState } from "react";

const SpotifyPlayer = ({ accessToken, currentTrack, setCurrentTrack, trackUri }) => {
	const [player, setPlayer] = useState(null);
	const [is_paused, setPaused] = useState(false);
	const [is_active, setActive] = useState(false);
	useEffect(() => {
		// Load the Spotify Web Playback SDK script
		const script = document.createElement("script");
		script.src = "https://sdk.scdn.co/spotify-player.js";
		script.async = true;
		document.body.appendChild(script);

		// Initialize the Spotify Web Playback SDK
		window.onSpotifyWebPlaybackSDKReady = () => {
			const player = new window.Spotify.Player({
				name: "React Spotify Player",
				getOAuthToken: (cb) => {
					cb(accessToken);
				},
			});

			// Save the player instance to the state
			setPlayer(player);

			// Connect to the player
			player.connect().then((success) => {
				if (success) {
					console.log("Connected to Spotify player!");
				}
			});
			player.addListener("ready", ({ device_id }) => {
				console.log("Ready with Device ID", device_id);
			});

			player.addListener("not_ready", ({ device_id }) => {
				console.log("Device ID has gone offline", device_id);
			});

			player.addListener("player_state_changed", (state) => {
				if (!state) {
					return;
				}

				setTrack(state.track_window.current_track);
				setPaused(state.paused);

				player.getCurrentState().then((state) => {
					!state ? setActive(false) : setActive(true);
				});
			});
		};

		// Cleanup on component unmount
		return () => {
			if (player !== null) {
				player.disconnect();
			}
		};
	}, [accessToken]);

	useEffect(() => {
		if (player !== null && trackUri) {
			// Play a new track
			player.resume();
			player.addListener("ready", ({ device_id }) => {
				player._options.id = device_id;
				player._options.getOAuthToken((access_token) => {
					fetch(`https://api.spotify.com/v1/me/player/play?device_id=${device_id}`, {
						method: "PUT",
						body: JSON.stringify({ uris: [trackUri] }),
						headers: {
							"Content-Type": "application/json",
							Authorization: `Bearer ${access_token}`,
						},
					});
				});
			});
		}
	}, [player, trackUri]);
	if (currentTrack != null) {
		return (
			<div id="spotify-player">
				<div className="main-wrapper">
					<img src={currentTrack.album.images[0].uri} className="now-playing__cover" alt="" />

					<div className="now-playing__side">
						<div className="now-playing__name">{currentTrack.name}</div>
						<div className="now-playing__artist">{currentTrack.artists[0].name}</div>

						<button
							className="btn-spotify"
							onClick={() => {
								player.previousTrack();
							}}
						>
							&lt;&lt;
						</button>

						<button
							className="btn-spotify"
							onClick={() => {
								player.togglePlay();
							}}
						>
							{is_paused ? "PLAY" : "PAUSE"}
						</button>

						<button
							className="btn-spotify"
							onClick={() => {
								player.nextTrack();
							}}
						>
							&gt;&gt;
						</button>
					</div>
				</div>
			</div>
		);
	} else {
		return <div></div>;
	}
};

export default SpotifyPlayer;
