import "bootstrap/dist/css/bootstrap.min.css";
import Login from "./Login";
import Dashboard from "./Dashboard";
import "./App.css";

// const code = new URLSearchParams(window.location.search).get("access_token");
// console.log(code);

function App() {
	const [token, setToken] = useState("");

	useEffect(() => {
		const hash = window.location.hash;
		let token = window.localStorage.getItem("token");

		// getToken()

		if (!token && hash) {
			token = hash
				.substring(1)
				.split("&")
				.find((elem) => elem.startsWith("access_token"))
				.split("=")[1];

			window.location.hash = "";
			window.localStorage.setItem("token", token);
		}

		setToken(token);
	}, []);

	const logout = () => {
		setToken("");
		window.localStorage.removeItem("token");
	};

	return (
		<div className="App">
			<header className="App-header">
				<h1>Spotify React</h1>
				{!token ? <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login to Spotify</a> : <button onClick={logout}>Logout</button>}

				{token ? (
					<form onSubmit={searchArtists}>
						<input type="text" onChange={(e) => setSearchKey(e.target.value)} />
						<button type={"submit"}>Search</button>
					</form>
				) : (
					<h2>Please login</h2>
				)}

				{renderArtists()}
			</header>
		</div>
	);
}

export default App;
