import React, { useState, useRef, useEffect } from "react";
import YouTube from "react-youtube";
import YoutubePlaylist from "./YoutubePlaylist";

const YoutubePlayer = ({ youtubePlaylist, handlePlayerReady, handlePlay, handlePause, handleNext, handlePrevious, handlePlaylistEnd, youtubeCurrentIndex, youtubeIsPlaying }) => {
	if (youtubePlaylist !== null) {
		console.log(youtubePlaylist);
		const songs = youtubePlaylist.songs.map((song) => song.songId);
		const opts = {
			playerVars: {
				autoplay: 0,
				controls: 0,
				listType: "playlist",
				list: songs.join(","),
			},
		};
		return (
			<div className="Youtube-Player">
				<YouTube videoId={youtubePlaylist[youtubeCurrentIndex]} opts={opts} onReady={handlePlayerReady} onEnd={handlePlaylistEnd} containerClassName="youtube-container" className="youtube-player" />
				<div className="player-controls">
					{!youtubeIsPlaying && <button onClick={handlePlay}>Play</button>}
					{youtubeIsPlaying && <button onClick={handlePause}>Pause</button>}
					<button onClick={() => handlePrevious(youtubePlaylist)}>Previous</button>
					<button onClick={() => handleNext(youtubePlaylist)}>Next</button>
				</div>
			</div>
		);
	}
};

export default YoutubePlayer;
