import { useState, useEffect } from "react";
import useAuth from "./useAuth";
import Player from "./Player";
import TrackSearchResult from "./TrackSearchResult";
import { Container, Form } from "react-bootstrap";
import SpotifyWebApi from "spotify-web-api-node";
import axios from "axios";
import Tracks from "./components/Tracks";
import ControlPanel from "./components/ControlPanel";

const API_BASE_URL = "https://api.spotify.com/v1";
const spotifyApi = new SpotifyWebApi({
	clientId: "8b945ef10ea24755b83ac50cede405a0",
});

export default function Dashboard({ code }) {
	/**-----------------------------------------------------------------------------------------------------------------------
	 *                                                    SPOTIFY
	 *-----------------------------------------------------------------------------------------------------------------------**/
	const accessToken = useAuth(code);
	const [search, setSearch] = useState("");
	const [searchResults, setSearchResults] = useState([]);
	const [playingTrack, setPlayingTrack] = useState();
	const [lyrics, setLyrics] = useState("");
	const [playlists, setPlaylists] = useState([]);
	const [playlistTracks, setPlaylistTracks] = useState([]);
	const [selectedPlaylist, setSelectedPlaylist] = useState(null);

	useEffect(() => {
		if (!accessToken) return;
		spotifyApi.setAccessToken(accessToken);
	}, [accessToken]);

	useEffect(() => {
		if (accessToken) {
			fetchPlaylists();
		}
	}, [accessToken]);

	const fetchPlaylists = async () => {
		try {
			const response = await fetch(`${API_BASE_URL}/me/playlists`, {
				headers: {
					Authorization: `Bearer ${accessToken}`,
				},
			});

			if (response.ok) {
				const data = await response.json();
				setPlaylists(data.items);
			} else {
				console.log("Error:", response.status);
			}
		} catch (error) {
			console.log("Error:", error);
		}
	};

	const fetchPlaylistTracks = async (playlistId) => {
		try {
			const response = await fetch(`${API_BASE_URL}/playlists/${playlistId}/tracks`, {
				headers: {
					Authorization: `Bearer ${accessToken}`,
				},
			});

			if (response.ok) {
				const data = await response.json();
				setPlaylistTracks(data.items);
			} else {
				console.log("Error:", response.status);
			}
		} catch (error) {
			console.log("Error:", error);
		}
	};

	const handlePlaylistSelect = async (playlist) => {
		setSelectedPlaylist(playlist);
		await fetchPlaylistTracks(playlist.id);
	};

	const chooseTrack = (track) => {
		setPlayingTrack(track);
		// setSearch("");
		// setLyrics("");
	};

	/**-----------------------------------------------------------------------------------------------------------------------
	 *                                                    YOUTUBE
	 *-----------------------------------------------------------------------------------------------------------------------**/

	return (
		<div className="dashboard">
			<hr />
			<Tracks selectedPlaylist={selectedPlaylist} playlistTracks={playlistTracks} chooseTrack={chooseTrack} />
			<ControlPanel playlists={playlists} handlePlaylistSelect={handlePlaylistSelect} />
			<div className="player">
				<Player accessToken={accessToken} trackUri={playingTrack?.uri} />
			</div>
			<hr style={{ bottom: "0", width: "100%" }} />
		</div>
	);
}

